<template><div><PageHeader title="动态审核" desc="通过/驳回"/><div class="card" v-for="p in posts" :key="p.id">{{p.content}}<button @click="rev(p,'approved')">通过</button><button @click="rev(p,'rejected')">驳回</button></div></div></template><script>import { postListApi,reviewPostApi } from '../../api/modules/post';import PageHeader from '../../components/PageHeader.vue';export default{components:{PageHeader},data(){return{posts:[]}},methods:{async load(){this.posts=(await postListApi()).data},async rev(p,s){await reviewPostApi({id:p.id,status:s});this.load()}},mounted(){this.load()}}</script>