<template><div><PageHeader title="用户管理" desc="筛选与状态"/><div class="card"><select v-model="role"><option value="">全部</option><option value="student">学生</option><option value="monitor">班长</option><option value="admin">管理员</option></select></div><ResponsiveTableCard :items="list"><template #default="{item}"><b>{{item.username}}</b>({{item.role}}) 状态:{{item.status?'启用':'禁用'}}</template></ResponsiveTableCard></div></template><script>import { adminUsersApi } from '../../api/modules/admin';import PageHeader from '../../components/PageHeader.vue';import ResponsiveTableCard from '../../components/ResponsiveTableCard.vue';export default{components:{PageHeader,ResponsiveTableCard},data(){return{all:[],role:''}},computed:{list(){return this.role?this.all.filter(i=>i.role===this.role):this.all}},async mounted(){this.all=(await adminUsersApi()).data}}</script>