<template><div><PageHeader title="加入班级" desc="输入班级码"/><div class="card"><input v-model="code" placeholder="班级码"/><button @click="join">加入</button></div></div></template><script>import PageHeader from '../../components/PageHeader.vue';import { joinClassApi } from '../../api/modules/class';export default{components:{PageHeader},data(){return{code:'CLS2020'}},methods:{async join(){const u=this.$store.state.auth.user;const r=await joinClassApi({code:this.code,userId:u.id});r.code===200?this.$message.success('加入成功'):this.$message.error(r.message)}}}</script>