<template><div><PageHeader title="成员名录" desc="支持搜索"/><div class="card"><input v-model="kw" placeholder="搜索"/></div><ResponsiveTableCard :items="list"><template #default="{item}"><b>{{item.username}}</b><p>{{item.role}}</p></template></ResponsiveTableCard></div></template><script>import PageHeader from '../../components/PageHeader.vue';import ResponsiveTableCard from '../../components/ResponsiveTableCard.vue';import { adminUsersApi } from '../../api/modules/admin';export default{components:{PageHeader,ResponsiveTableCard},data(){return{all:[],kw:''}},computed:{list(){return this.all.filter(i=>i.username.includes(this.kw)||i.nickname?.includes(this.kw))}},async mounted(){this.all=(await adminUsersApi()).data}}</script>