<template><div><PageHeader title="个人中心" desc="资料设置"/><div class="card"><UploadMock v-model="user.avatar"/><input v-model="user.nickname"/><input v-model="user.signature"/><button @click="save">保存</button></div></div></template><script>import PageHeader from '../../components/PageHeader.vue';import UploadMock from '../../components/UploadMock.vue';export default{components:{PageHeader,UploadMock},data(){return{user:{...this.$store.state.auth.user}}},methods:{save(){localStorage.setItem('user',JSON.stringify(this.user));this.$store.commit('auth/setAuth',{token:this.$store.state.auth.token,user:this.user});this.$message.success('已保存')}}}</script>